package com.liuwentao.bililottery.util;import java.util.HashMap;import java.util.concurrent.RunnableScheduledFuture;/** * Created by liuwentao on 2021/10/6 14:18 * * 存放所有定时任务的map单例，方便用户取消已经发起的定时抽奖 * key: openId + "+" + id; value: 发起定时任务返回的 RunnableScheduledFuture<?> 对象 */public class ScheduledTaskMapSingleTon {    private HashMap<String, RunnableScheduledFuture<?>> scheduledFutureHashMap = new HashMap<>();    private static ScheduledTaskMapSingleTon scheduledTaskMapSingleTon = new ScheduledTaskMapSingleTon();    private ScheduledTaskMapSingleTon(){}    public static ScheduledTaskMapSingleTon getScheduledTaskMapSingleTon() {        return scheduledTaskMapSingleTon;    }    public static void setScheduledTaskMapSingleTon(ScheduledTaskMapSingleTon scheduledTaskMapSingleTon) {        ScheduledTaskMapSingleTon.scheduledTaskMapSingleTon = scheduledTaskMapSingleTon;    }    public HashMap<String, RunnableScheduledFuture<?>> getScheduledFutureHashMap() {        return scheduledFutureHashMap;    }}